import {validateUser,validateLogin} from "../models/user";
import userService from "../services/userService";
import _ from 'lodash';

class UserController {
  async registerUser(req, res) {
    try {
      const { error } = validateUser(req.body);
      if (error) return res.status(400).send(error.details[0].message);

      let user = await userService.register(req.body);

      if (user) {
        res.status(201).json({ message: "Success", user: _.pick(user,['_id','email','fullName','userName']) });
      }
    } catch (error) {
      res
        .status(error.status || 500)
        .send(error.message || "Something went wrong");
    }
  }

  async login (req,res) {
    try {
      const { error } = validateLogin(req.body);
      if (error) return res.status(400).send(error.details[0].message);

      let result = userService.login(req.body);

      res.status(200).json(result);
    } catch (error) {
      res
      .status(error.status || 500)
      .send(error.message || "Something went wrong");
    }
  } 
}


export default new UserController();

